import React from "react";
import { useState } from "react";
export default function BookStore(){
    const [searchQuery, setSearchQuery] = useState("");
    const [bookDisplay, setBookDisplay] = useState([]);
    function keyHandle(event){
        const data = {};
        if(event.key ==="Enter"){
            try{
                fetchData = async() => {
                    const query = searchQuery;
                    const response = await fetch("https://openlibrary.org/search.json?q=the+lord+of+the+rings");
                    data = await response.json();
                }
                fetchData().then(()=>{
                    //Display books here. 
                    console.log(data.docs[0].title);
                });
            }catch(err){
                console.log(err);
            }
        }
    }
    return(
        <div className = "book-store-page">
            <div className = "navigation-bar">
                <h1>Book browse</h1>
                <input className = "search-bar" 
                    type = "text" 
                    placeholder = "Search for titles, author, etc."
                    onKeyDown={keyHandle}
                    value = {searchQuery}
                    onChange = {(e)=>setSearchQuery(e.target.value)}
                    ></input>
                <button className = "category-browse-button">Browse by category</button>
                <button className = "your-library-button">Your library</button>
            </div>

        </div>
    )
}
